          if (err) {
            return next(new Error('ERE009'));
          } else {
            for (var i = 0; input.length > i; i++) {
              if (input[i]._id) {
                index = corp.departments.findIndex(x => x._id == input[i]._id);
                corp.departments[index] = input[i];
              } else {
                corp.departments.push(input[i]);
