                      if (department.name === departmentDB.name) {
                        departmentDB.qrCode.push(qrCode);
                      }
                    });
                  });
                });
                corp.residuesRegister = res.residuesRegister;
                corp.update(corp).then(x => {
                  resolve(corp);
