var Corporation = require('../../../models/corporation.model');
var Collector = require('../../../models/collector.model');
var Provider = require('../../../models/provider.model');
var CheckPoint = require('../../../models/checkpoint.model');
var TransactionHistory = require('../../../models/transactionhistory.model');
mongoose = require('mongoose');

var Classification = {
	Provider: 'Fornecedor',
	Collector: 'Empresa Coletora'
};

module.exports = corporation = {
	Query: {
		async getCorporation(root, { _id }) {
			return await Corporation.findById(_id);
		},
		async getCorporationByUser(root, { _id }) {
			var res = await Corporation.findOne({ 'users._id': _id });
			if (!res) {
				return undefined;
			} else {
				return res;
			}
		},
		async allCorporations() {
			return await Corporation.find();
		},

		async allUnits(root, { _id }) {
			var res = await Corporation.findById(_id);
			if (res) {
				var unit;
				var units = [];
				for (var i = 0; res.units.length > i; i++) {
					unit = undefined;
					unit = await Corporation.findById(res.units[i].unitsId);
					if (!unit) {
						unit = await Collector.findById(res.units[i].unitsId);
					}
					if (!unit) {
						unit = await Provider.findById(res.units[i].unitsId);
					}

					if (unit) {
						units.push(unit);
					}
				}
				return units;
			} else {
				return undefined;
			}
		},

		async allDocuments(root, { _id }) {
			var res = await Corporation.findById(_id);
			if (res) {
				return res.documents;
			} else {
				return undefined;
			}
		},

		async allDepartments(root, { _id }) {
			var res = await Corporation.findById(_id);
			if (res) {
				return res.departments;
			} else {
				return undefined;
			}
		},
		async allResiduesRegister(root, { _id }) {
			var res = await Corporation.findById(_id);
			if (res) {
				return res.residuesRegister;
			} else {
				return undefined;
			}
		},
		async allSchedulings(root, { _id }) {
			var res = await Corporation.findById(_id);
			if (res) {
				return res.scheduling;
			} else {
				return undefined;
			}
		},
		async allEntries(root, { _id }) {
			var res = await Corporation.findById(_id);
			if (res) {
				return res.entries;
			} else {
				return undefined;
			}
		},
		async allProvidersId(root, { _id }) {
			var res = await Corporation.findById(_id);
			if (res) {
				return res.myProviders;
			} else {
				return undefined;
			}
		}
	},
	Mutation: {
		async createCorporation(root, { input }) {
			var res = await Corporation.findOne({ 'users.email': input.users[0].email });
			if (res) {
				throw new Error('WRE005');
			} else {
				return await Corporation.create(input);
			}
